"use strict";

var _fixtures = require("../test/fixtures");

var _utils = require("./utils");

describe("segment", function () {
  test("address segmentation", function () {
    // bc1qdx0pd4h65d7mekkhk7n6jwzfwgqath7s0e368g
    // -> bc1qdx 0pd4h65d7mekkhk7n6jwzfwgqath7s 0e368g
    var result = ["bc1qdx", "0pd4h65d7mekkhk7n6jwzfwgqath7s", "0e368g"];
    expect((0, _utils.segment)(_fixtures.KEY.MAIN.BECH32)[0].length).toBe(6);
    expect((0, _utils.segment)(_fixtures.KEY.MAIN.BECH32)[2].length).toBe(6);
    expect((0, _utils.segment)(_fixtures.KEY.MAIN.BECH32)).toStrictEqual(result);
  });
});