"use strict";

var _block_explorer = require("./block_explorer");

var _networks = require("./networks");

describe("block_explorer", function () {
  describe("blockExplorerURL", function () {
    it('should properly return the base mainnet block explorer url for empty path', function () {
      expect((0, _block_explorer.blockExplorerURL)("", _networks.MAINNET)).toBe("https://blockstream.info");
    });
    it('should properly return the mainnet block explorer url for a given path path', function () {
      var path = "/block/00000000000000000011341d69792271766e4683e29b3ea169eacc59bde10a57";
      expect((0, _block_explorer.blockExplorerURL)(path, _networks.MAINNET)).toBe("https://blockstream.info".concat(path));
    });
    it('should properly return the base testnet block explorer url for empty path', function () {
      expect((0, _block_explorer.blockExplorerURL)("", _networks.TESTNET)).toBe("https://blockstream.info/testnet");
    });
    it('should properly return the testnet block explorer url for a given path path', function () {
      var path = "/tx/4f0ef69f88829bd2f6b7793e32dd8bfcfbc87ddb9a2de3d8ef3f2aabbaff0be3";
      expect((0, _block_explorer.blockExplorerURL)(path, _networks.TESTNET)).toBe("https://blockstream.info/testnet".concat(path));
    });
  });
  describe("blockExplorerAPIURL", function () {
    it('should properly return the mainnet block explorer api url for path', function () {
      var path = "/tx/1814a10fb22e9551a17a94a1e68971e19b4f59eaf1689e0af85b97929b3b9ae0";
      expect((0, _block_explorer.blockExplorerAPIURL)(path, _networks.MAINNET)).toBe("https://blockstream.info/api".concat(path));
    });
    it('should properly return the testnet block explorer api url for path', function () {
      var path = "/tx/4f0ef69f88829bd2f6b7793e32dd8bfcfbc87ddb9a2de3d8ef3f2aabbaff0be3";
      expect((0, _block_explorer.blockExplorerAPIURL)(path, _networks.TESTNET)).toBe("https://blockstream.info/testnet/api".concat(path));
    });
  });
  describe("blockExplorerTransactionURL", function () {
    it('should properly return the mainnet block explorer transaction url for a given path', function () {
      var path = "1814a10fb22e9551a17a94a1e68971e19b4f59eaf1689e0af85b97929b3b9ae0";
      expect((0, _block_explorer.blockExplorerTransactionURL)(path, _networks.MAINNET)).toBe("https://blockstream.info/tx/".concat(path));
    });
    it('should properly return the testnet block explorer transaction url for a given path', function () {
      var path = "4f0ef69f88829bd2f6b7793e32dd8bfcfbc87ddb9a2de3d8ef3f2aabbaff0be3";
      expect((0, _block_explorer.blockExplorerTransactionURL)(path, _networks.TESTNET)).toBe("https://blockstream.info/testnet/tx/".concat(path));
    });
  });
  describe("blockExplorerAddressURL", function () {
    it('should properly return the mainnet block explorer address url for a given path', function () {
      var path = "39YqNoLULDpbjmeCTdGJ42DQhrQLzRcMdX";
      expect((0, _block_explorer.blockExplorerAddressURL)(path, _networks.MAINNET)).toBe("https://blockstream.info/address/".concat(path));
    });
    it('should properly return the testnet block explorer address url for a given path', function () {
      var path = "2N16oE62ZjAPup985dFBQYAuy5zpDraH7Hk";
      expect((0, _block_explorer.blockExplorerAddressURL)(path, _networks.TESTNET)).toBe("https://blockstream.info/testnet/address/".concat(path));
    });
  });
});