"use strict";

var _networks = require("./networks");

var bitcoin = require('bitcoinjs-lib');

describe("networks", function () {
  describe("networkLabel", function () {
    it("returns a human-readable network name", function () {
      expect((0, _networks.networkLabel)(_networks.MAINNET)).toBe("Mainnet");
      expect((0, _networks.networkLabel)(_networks.TESTNET)).toBe("Testnet");
      expect((0, _networks.networkLabel)("foobar")).toBe("Testnet");
    });
  });
  describe("networkLabel", function () {
    it("returns a human-readable network name", function () {
      expect((0, _networks.networkData)(_networks.MAINNET)).toBe(bitcoin.networks.bitcoin);
      expect((0, _networks.networkData)(_networks.TESTNET)).toBe(bitcoin.networks.testnet);
      expect((0, _networks.networkData)("foobar")).toBe(bitcoin.networks.testnet);
    });
  });
});