"use strict";

var _p2sh = require("./p2sh");

describe("p2sh", function () {
  describe('estimateMultisigP2SHTransactionVSize', function () {
    it('estimates the transaction size in vbytes', function () {
      expect((0, _p2sh.estimateMultisigP2SHTransactionVSize)({
        numInputs: 1,
        numOutputs: 2,
        m: 2,
        n: 3
      })).toBe(391);
    });
  });
});