"use strict";

var _p2sh_p2wsh = require("./p2sh_p2wsh");

describe("p2sh_p2wsh", function () {
  describe('estimateMultisigP2SH_P2WSHTransactionVSize', function () {
    it('estimates the transaction size in vbytes', function () {
      expect((0, _p2sh_p2wsh.estimateMultisigP2SH_P2WSHTransactionVSize)({
        numInputs: 2,
        numOutputs: 2,
        m: 3,
        n: 5
      })).toBe(444); // actual value from bitcoin core for P2PKH out
    });
  });
});