"use strict";

var _script = require("./script");

var _fixtures = require("./fixtures");

describe("scripts", function () {
  describe("scriptToOps", function () {
    _fixtures.TEST_FIXTURES.multisigs.forEach(function (test) {
      it("returns the opcodes for a 2-of-2 ".concat(test.network, " ").concat(test.type, " address"), function () {
        expect((0, _script.scriptToOps)(test.multisig)).toEqual(test.scriptOps);
        expect((0, _script.scriptToOps)(test.multisigScript)).toEqual(test.multisigScriptOps);
      });
    });
  });
  describe("scriptToHex", function () {
    _fixtures.TEST_FIXTURES.multisigs.forEach(function (test) {
      it("returns the hex for a 2-of-2 ".concat(test.network, " ").concat(test.type, " address"), function () {
        expect((0, _script.scriptToHex)(test.multisig)).toEqual(test.scriptHex);
        expect((0, _script.scriptToHex)(test.multisigScript)).toEqual(test.multisigScriptHex);
      });
    });
  });
});